	let title = "Sistema de Evaluación de Desempeño";
	$('#tablaReportes').DataTable({
		language: {
			"decimal": ",",
			"emptyTable": "No hay información",
			"info": "Mostrando _START_ a _END_ de _TOTAL_ Entradas",
			"infoEmpty": "Mostrando 0 to 0 of 0 Entradas",
			"infoFiltered": "(Filtrado de _MAX_ total entradas)",
			"infoPostFix": "",
			"thousands": ",",
			"lengthMenu": "Mostrar _MENU_ Entradas",
			"loadingRecords": "Cargando...",
			"processing": "Procesando...",
			"search": "Buscar:",
			"zeroRecords": "No se encontraron resultados",
			"paginate": {
				"first": "Primero",
				"last": "Ultimo",
				"next": "Siguiente",
				"previous": "Anterior"
			}

		},
		dom: '<"dt-buttons">Bf<"clear">lrtp',
		paging: true,
		autoWidth: true,
		buttons: [{
				extend: 'excelHtml5',
				text: '<i class="fas fa-file-excel"></i>',
				titleAttr: 'Exportar a Excel',
				title: title,
				className: 'btn btn-lg btn-icon rounded-1 btn-success me-1 w-25 d-flex flex-row justify-content-center align-items-center',
			},
			{
				text: '<i class="fas fa-file-pdf"></i>',
				extend: 'pdfHtml5',
				filename: title,
				className: 'btn btn-lg btn-icon rounded-1 btn-danger me-1 w-25 d-flex flex-row justify-content-center align-items-center',
				orientation: 'portrait', //landscape
				pageSize: 'A4', //A3 , A5 , A6 , legal , letter
				exportOptions: {
					columns: ':visible',
					search: 'applied',
					order: 'applied'
				},
				customize: function(doc) {
					//Remove the title created by datatTables
					doc.content.splice(0, 1);
					//Create a date string that we use in the footer. Format is dd-mm-yyyy
					var now = new Date();
					var jsDate = now.getDate() + '-' + (now.getMonth() + 1) + '-' + now.getFullYear();
					// Logo converted to base64
					// var logo = getBase64FromImageUrl('https://datatables.net/media/images/logo.png');
					// The above call should work, but not when called from codepen.io
					// So we use a online converter and paste the string in.
					// Done on http://codebeautify.org/image-to-base64-converter
					// It's a LONG string scroll down to see the rest of the code !!!
					// var logo = "data:image/jpeg;base64,/9j/4RbYWx" // A documentation reference can be found at
					// https://github.com/bpampuch/pdfmake#getting-started
					var logo = "data:image/jpeg;base64,";
					// Set page margins [left,top,right,bottom] or [horizontal,vertical]
					// or one number for equal spread
					// It's important to create enough space at the top for a header !!!
					doc.pageMargins = [20, 60, 20, 30];
					// Set the font size fot the entire document
					doc.defaultStyle.fontSize = 10;
					// Set the fontsize for the table header
					doc.styles.tableHeader.fontSize = 12;
					// Create a header object with 3 columns
					// Left side: Logo
					// Middle: brandname
					// Right side: A document title
					doc['header'] = (function() {
						return {
							columns: [{
									image: logo,
									width: 32
								},
								{
									alignment: 'left',
									italics: true,
									text: 'Sistema de Evaluación de Desempeño de Personal',
									fontSize: 15,
									margin: [10, 0]
								},
								{
									alignment: 'right',
									fontSize: 13,
									text: jsDate.toString(),
								}
							],
							margin: 20
						}
					});

					// Create a footer object with 2 columns
					// Left side: report creation date
					// Right side: current page and total pages
					doc['footer'] = (function(page, pages) {
						return {
							columns: [{
									alignment: 'left',
									text: ['Creado en: ', {
										text: jsDate.toString()
									}]
								},
								{
									alignment: 'right',
									text: ['página ', {
										text: page.toString()
									}, ' de ', {
										text: pages.toString()
									}]
								}
							],
							margin: 20
						}
					});
					// Change dataTable layout (Table styling)
					// To use predefined layouts uncomment the line below and comment the custom lines below
					// doc.content[0].layout = 'lightHorizontalLines'; // noBorders , headerLineOnly
					var objLayout = {};
					objLayout['hLineWidth'] = function(i) {
						return .5;
					};
					objLayout['vLineWidth'] = function(i) {
						return .5;
					};
					objLayout['hLineColor'] = function(i) {
						return '#aaa';
					};
					objLayout['vLineColor'] = function(i) {
						return '#aaa';
					};
					objLayout['paddingLeft'] = function(i) {
						return 4;
					};
					objLayout['paddingRight'] = function(i) {
						return 4;
					};
					doc.content[0].layout = objLayout;
				}
			},
			{
				extend: 'print',
				text: '<i class="fa fa-print"></i>',
				titleAttr: 'Imprimir',
				title: title,
				className: 'btn btn-lg btn-icon rounded-1 btn-secondary w-25 d-flex flex-row justify-content-center align-items-center',
			},

		]
	});
</script>